pty.mouseout_confirm = function(e) {
	var x = e.clientX;
	var y = e.clientY;
	if (x > pty.lastX) {
		moving_left = true;
		moving_right = false;
	}
	else {
		moving_right = false;
		moving_left = true;
	}
	if (y > pty.lastY) {
		moving_up = false;
		moving_down = true;
	}
	else {
		moving_up = true;
		moving_down = false;
	}
	pty.mouseout_confirmed = false
	if (($j(window).width() - x) > 50 || !moving_left) {
		if (moving_up && y < 50) {
			pty.mouseout_confirmed = true
		}
		if (moving_left && x < 50) {
			pty.mouseout_confirmed = true
		}
		if (moving_down && y > $j(window).height() - 50) {
			pty.mouseout_confirmed = true
		}
	}
	pty.lastX = x;
	pty.lastY = y;
};
